    var request = require(&#39;request&#39;);
    const AWS = require(&#39;aws-sdk&#39;);
    AWS.config.update({ region: &#39;eu-north-1&#39; });
    var lambda = new AWS.Lambda({ region: &#39;eu-north-1&#39; });
    var s3 = new AWS.S3();

    exports.handler = (event, context, callback) =&gt; {

    var result = [];
    var counter = 0;

    var params = {
        MaxItems: 100
    };

    lambda.listFunctions(params, function (err, lambdaFunctions) {
        if (err) console.log(err, err.stack); // an error occurred
        else {

            // We got the functions now lets get the urls
            lambdaFunctions.Functions.forEach(LambdaFunc =&gt; {

                var params = {
                    FunctionName: LambdaFunc.FunctionName
                };

                lambda.getFunction(params, function (err, data) {
                    if (err) console.log(err, err.stack); // an error occurred
                    else {


                        var temp = { Code: data.Code, Configuration: data.Configuration };
                        result.push(temp);

                        counter++;
                        if (counter == lambdaFunctions.Functions.length) {

                            downloadToS3();

                        }
                    }
                });
            });
        }
    });

    function downloadToS3() {

        result.forEach(data =&gt; {

            var FunctionName = data.Configuration.FunctionName;
            console.log(FunctionName);
            var path = &quot;lambda/&quot; + FunctionName + &quot;.zip&quot;;
            var bucket = &quot;YOUR-BUCKET-NAME&quot;;

            var Location = data.Code.Location;
