      var href = $(this).attr(&#39;href&#39;);
      if (/\.pdf$/.test(href))
        return &#39;pdf&#39;;
        
      if (/(\.zip|\.exe)$/.test(href))
        return&#39;download&#39;;
      
      return &#39;external&#39;;
    });

