    cpp_fun.funName.argtypes = [np.ctypeslib.ndpointer(),
    							np.ctypeslib.ndpointer(),
      						    ctypes.c_int]
        
    # Allocate or define the input variables using numpy or the standard scalar of python (int, float, ecc..)
