let chart = new Chart(document.getElementById(&quot;chart&quot;), {
  type: &#39;line&#39;,
  data: data,  
  options: {
    scales: {
      yAxes: [{
        ticks: {
          min: 0,
          max: 100
        }
      }]
    }
  }
});

function addDataButton(){
  addData()
  chart.update()
}
