  } else {
    console.log(&quot;correct&quot;);
  }
});
$(&#39;#response&#39;).on(&#39;ended&#39;, () =&gt; {
  console.log(&#39;too late&#39;);
  $(&#39;#response&#39;).off(&#39;click&#39;);
});
