      var fillRect = true;
      ctx.rect(rect.x, rect.y, rect.w, rect.h);
      if (fillRect) {
        ctx.fill();
      }
      ctx.stroke();
      if (currentHandle) {
        var posHandle = point(0, 0);
        switch (currentHandle) {
