    class MetaLevel(type):
        def __new__(mcs, name, base, attrs):
            cls = super().__new__(mcs, name, base, attrs)
    
            s = cls.something
            for c in base:
                s += c.something
    
            setattr(cls, &#39;something&#39;, s)
    
            return cls
    
    
    class Level1(metaclass=MetaLevel):
        something = [&#39;abc&#39;, &#39;def&#39;]
    
    
    class Level2(Level1):
        something = [&#39;ghi&#39;]
    
    
    class Level3(Level2):
        something = [&#39;jkl&#39;]
    
    
    
    print(Level3.something)
    # output: [&#39;abc&#39;, &#39;def&#39;, &#39;ghi&#39;, &#39;jkl&#39;]

