def index(request):
    ...
    dec = {&#39;name&#39;:name_data, &#39;price&#39;:price_data, &#39;image&#39;:image_data, &#39;url&#39;:url}
    url = &#39;{}?{}&#39;.format(reverse(&#39;data&#39;), urllib.parse.urlencode(dec)) # /data/?name=abc&amp;price=45 ..
    return redirect(url)
