  int *p2;
  p2 = new int[10];
  cout&lt;&lt;sizeof(*p2)&lt;&lt;endl;  // 4 size of the first element (int)
  cout&lt;&lt;sizeof(p2)&lt;&lt;endl;  // 8 size of the pointer on 64-bit system
  
  // shows how you&#39;re getting the first value by dereferencing
  cout&lt;&lt;*p2&lt;&lt;endl; // 0
  p2[0] = 100;
  cout&lt;&lt;*p2&lt;&lt;endl; // 100
