                .pipe(
                  // Mouseup = Release do clique esquerdo
                  takeUntil(fromEvent(componente, &#39;mouseup&#39;)),
                  // Mouseleave = Quando o mouse sai da &#225;rea do Componente
                  takeUntil(fromEvent(componente, &#39;mouseleave&#39;)),
                  pairwise()
                );
            })
          )
          .subscribe((res: [MouseEvent, MouseEvent]) =&gt; {
