          fig_1_value = $el_1.find(&#39;.top&#39;).html(),
          fig_2_value = $el_2.find(&#39;.top&#39;).html();

        if (value &gt;= 10) {
          // Animate only if the figure has changed
          if (fig_1_value !== val_1) this.animateFigure($el_1, val_1);
