    // Create Countdown
    var Countdown = {
      
      // Backbone-like structure
      $el: $(&#39;.countdown&#39;),
      
      // Params
      countdown_interval: null,
      total_seconds     : 0,
      
      // Initialize the countdown  
      init: function() {
        
        // DOM
    		this.$ = {
        	hours  : this.$el.find(&#39;.bloc-time.hours .figure&#39;),
        	minutes: this.$el.find(&#39;.bloc-time.min .figure&#39;),
