        FlutterEventChannel* secondEventChannel =
            [FlutterEventChannel eventChannelWithName:@&quot;my_second_event_channel&quot;
                                      binaryMessenger:controller];
        [secondEventChannel setStreamHandler:secondStreamHandler];
        
