    private static List&lt;String&gt; permutations;
    
    public static void main(String[] args) {
    	permutations = new ArrayList&lt;&gt;();
    	printPermutn(&quot;123&quot;, &quot;&quot;);
    
    	System.out.println();
    	System.out.println(&quot;permutations BEFORE sorting&quot;);
    	System.out.println(permutations);
    	
    	// sort
