    template &lt;int x&gt;
    class A {
    public:
      template &lt;int y, int = y&gt; class B;
    };
    
    template &lt;int x&gt;
    template &lt;int y, int z&gt;
    class A&lt;x&gt;::B {
    };
    
    int main() {
