                   &quot;must&quot;:[
                      {
                         &quot;match&quot;:{
                            &quot;authors.firstName&quot;:{
                               &quot;query&quot;:&quot;john&quot;
                            }
                         }
                      },
                      {
                         &quot;match&quot;:{
                            &quot;authors.lastName&quot;:{
