if(!preg_match(&quot;/^(?:2[0-3]|[01][0-9]|[0-9]):[0-5][0-9]$/&quot;, $StartTime) || !preg_match(&quot;/^(?:2[0-3]|[01][0-9]|[0-9]):[0-5][0-9]$/&quot;, $EndTime)){
