	    for (int i = 0; i &lt; l; i++) {
		    if (s[i] == &#39;-&#39;) {
			    if (minus) {
				    return false;
			    }
			    minus = true;
		    } else if (s[i] == &#39;0&#39;) {
			    if (minus) {
			    	return false;
			    }
		    } else if (s[i] == &#39;1&#39;) {
			    if (minus) {
			    	minus = false;
			    }
		    } else {
			    return false;
		    }
	    }
	    return !minus;
    }

Here&#39;s a terser version of Mihail&#39;s suggestion:

    bool BTernary::isTernary(const std::string&amp; s)
    {
        bool minus = false;
        
        for (const char c : s)
        {
           switch (c)
           {
             case &#39;-&#39;:
                if (minus) return false;
                minus = true;
                break;
            
             case &#39;0&#39;:
                if (minus) return false;
                break;
            
             case &#39;1&#39;:
                if (minus) minus = false;
                break;
            
             default:
                return false;
        }
        
        return !minus;
