  onClick(event: MouseEvent){
    // console.log(event.target)
    let clickedEle = event.target as HTMLElement;
    let aBoxEle = this.eleRef.nativeElement.querySelector(&#39;#aBox&#39;) as HTMLElement;
    if(clickedEle.id != &quot;aBox&quot;){
