	  SimpleMember member;

      public:
	  virtual void set_member(int m) = 0;
    };

    struct Derived : public Base
    {
	  MoreElaborateMember member;

	  virtual void set_member(int m)
	  {
		Base::member.a = m;
		member.a = m;
	  }
    };

    void foo(Base* base)
    {
