// draw.c
void shade(struct bitmap bmp, double variance)
{
    // A flag for each pixel to indicate if it was processed
    uint8_t *flags = calloc(bmp.info.bmp_size / 3, sizeof(uint8_t));
    // A queue for pixels in the current region that still need to be processed
    struct point *queue = malloc(bmp.info.bmp_size * sizeof(struct point) / 3);
    
    // Loop over every pixel
    for (int y = 0; y &lt; bmp.info.height; y++) {
        for (int x = 0; x &lt; bmp.info.width; x++) {
            // If this pixel is already done, skip it
            if (flags[x + y * bmp.info.width]) {
                continue;
            }
            int qp = 0; // Queue pointer/position

            // Get the colour to shade next, skip if black or invalid pixel
            union bmp_colour target = get_pixel(bmp, x, y);
            if (target.colour == 0x000000 || target.reserved) {
                continue;
            }

            // Get the colour to fill this region with
            union bmp_colour fill_col = { .colour = target.colour };
            double var = rand_between(1 - variance, 1 + variance);
            fill_col.r = (int) clamp(var * fill_col.r, 0x00, 0xff);
            fill_col.g = (int) clamp(var * fill_col.g, 0x00, 0xff);
            fill_col.b = (int) clamp(var * fill_col.b, 0x00, 0xff);

            // Add the current point to the queue
            queue[qp] = (struct point) { .x = x, .y = y}; 

            do {
                int cx = queue[qp].x, cy = queue[qp].y;
                // Mark this pixel as done
                flags[cx + cy * bmp.info.width] = 1;
                
                // Set this pixel to the new colour
                set_pixel(bmp, cx, cy, fill_col.colour);

                // Get each surrounding pixel
                union bmp_colour left = get_pixel(bmp, cx + 1, cy);
                union bmp_colour right = get_pixel(bmp, cx - 1, cy);
                union bmp_colour up = get_pixel(bmp, cx, cy + 1);
                union bmp_colour down = get_pixel(bmp, cx, cy - 1);

                // For each surrounding pixel, if it is the &quot;target&quot; colour,
                // add it to the queue and increment qp
                if (!left.reserved &amp;&amp; left.colour == target.colour
                        &amp;&amp; flags[cx + 1 + cy * bmp.info.width] == 0) {
                    queue[qp++] = (struct point) { .x = cx + 1, .y = cy };
                }
                if (!right.reserved &amp;&amp; right.colour == target.colour
                        &amp;&amp; flags[cx - 1 + cy * bmp.info.width] == 0) {
                    queue[qp++] = (struct point) { .x = cx - 1, .y = cy };
                }
                if (!up.reserved &amp;&amp; up.colour == target.colour
                        &amp;&amp; flags[cx + (cy + 1) * bmp.info.width] == 0) {
                    queue[qp++] = (struct point) { .x = cx, .y = cy + 1 };
                }
                if (!down.reserved &amp;&amp; down.colour == target.colour
                        &amp;&amp; flags[cx + (cy - 1) * bmp.info.width] == 0) {
                    queue[qp++] = (struct point) { .x = cx, .y = cy - 1 };
                }
            } while (qp-- &gt; 0); // Repeat until the queue is empty
        }
    }
}
