      const colors = [Colors.Blue, Colors.Green, Colors.Yellow];
      const [color, setColor] = React.useState(0);
      const { current } = React.useRef({ next: function() { } });
    
      current.next = () =&gt; {
        const nextColor = (color === 2) ? 0 : (color + 1);
        setColor(nextColor);
      };
    
      useEffect(
