    $(&#39;a[data-toggle=&quot;tab&quot;]&#39;).on(&#39;shown.bs.tab&#39;, function (e) {
      $(e.target).addClass(&#39;active&#39;).siblings().removeClass(&quot;active&quot;); // activated tab
    });

