    cout &lt;&lt; std::hex &lt;&lt; properI1 &lt;&lt; endl;
    cout &lt;&lt; std::hex &lt;&lt; properI2 &lt;&lt; endl;
    cout &lt;&lt; std::hex &lt;&lt; dodgyI1 &lt;&lt; endl;

