With the help from this question: [https://stackoverflow.com/questions/4770025/how-to-disable-scrolling-temporarily](https://stackoverflow.com/questions/4770025/how-to-disable-scrolling-temporarily) it was pretty easy to make a working concept:

&lt;!-- begin snippet: js hide: false console: false babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    var keys = {37: 1, 38: 1, 39: 1, 40: 1};

    // Select all input elements
    var inputElems = document.getElementsByTagName(&#39;input&#39;);

    // Turn them into an array
    inputElems = Array.prototype.slice.call(inputElems);

    // Create event listeners for input elements where type equals number
    inputElems.forEach(function(elem) {
    	if(elem.type.toLowerCase() == &#39;number&#39;) {
      	elem.addEventListener(&#39;focus&#39;, disableScroll, false);
        elem.addEventListener(&#39;blur&#39;, enableScroll, false);
      }
    });

    function preventDefault(e) {
      e = e || window.event;
      if (e.preventDefault)
          e.preventDefault();
      e.returnValue = false;  
    }

    function preventDefaultForScrollKeys(e) {
        if (keys[e.keyCode]) {
            preventDefault(e);
            return false;
        }
    }

    function disableScroll() {
      if (window.addEventListener) // older FF
          window.addEventListener(&#39;DOMMouseScroll&#39;, preventDefault, false);
      document.addEventListener(&#39;wheel&#39;, preventDefault, {passive: false}); // Disable scrolling in Chrome
      window.onwheel = preventDefault; // modern standard
      window.onmousewheel = document.onmousewheel = preventDefault; // older browsers, IE
      window.ontouchmove  = preventDefault; // mobile
      document.onkeydown  = preventDefaultForScrollKeys;
    }

    function enableScroll() {
        if (window.removeEventListener)
            window.removeEventListener(&#39;DOMMouseScroll&#39;, preventDefault, false);
        document.removeEventListener(&#39;wheel&#39;, preventDefault, {passive: false}); // Enable scrolling in Chrome
        window.onmousewheel = document.onmousewheel = null; 
        window.onwheel = null; 
        window.ontouchmove = null;  
        document.onkeydown = null;  
    }

