      get x () {
        return `${this._x}px`;
      }
    
      get y () {
        return `${this._y}px`;
      }
    
      set x(xVal: string) {
        const num = xVal.match(VALID_XY_VAL);
        if (num === null) throw new Error(&#39;Invalid value&#39;);
        this._x = Number(num[1]);
      }
    
      set y(yVal: string) {
        const num = yVal.match(VALID_XY_VAL);
        if (num === null) throw new Error(&#39;Invalid value&#39;);
        this._y = Number(num[1]);
      }
    }

    const p = new Point(&#39;1px&#39;, &#39;2px&#39;); // Point&lt;1, 2&gt;
    p.x; // &#39;1px&#39;
    p.y; // &#39;2px&#39;
