A little lengthier but exactly the way how recursion works:

    function makeTree(categories, parent) {
      const stack = [{ parent }];
      let subnode; // the return value
      call: while (stack.length) {
        let { parent, node, i, c } = stack.pop();
        if (!node) {
          node = {};
          i = 0;
        } else {
          node[c.id] = subnode;
        }
        for (; i &lt; categories.length; i++) {
          const category = categories[i];
          if (category.parent === parent) {
            stack.push({
              parent,
              node,
              i: i+1,
              c: category
            });
            stack.push({
              parent: category.id
            });
            continue call;
          }
        }
        subnode = node;
      }
      return subnode;
    }

