function _custom_nav_menu_item( $title, $url, $order, $parent = 0 ){
  $item = new stdClass();
  $item-&gt;ID = 1000000 + $order + $parent;
  $item-&gt;db_id = $item-&gt;ID;
  $item-&gt;title = $title;
  $item-&gt;url = $url;
