  $item-&gt;classes = array();
  $item-&gt;target = &#39;&#39;;
  $item-&gt;attr_title = &#39;&#39;;
  $item-&gt;description = &#39;&#39;;
  $item-&gt;xfn = &#39;&#39;;
  $item-&gt;status = &#39;&#39;;
  return $item;
}
add_filter(&quot;wp_get_nav_menu_items&quot;, function ($items, $menu, $args) {
	if( $menu-&gt;term_id != 24 ) return $items; // Where 24 is Menu ID, so the code won&#39;t affect other menus.

	// don&#39;t add child categories in administration of menus
	if (is_admin()) {
		return $items;
