			$new_item = _custom_nav_menu_item( $term-&gt;name, get_term_link($term), $ctr, $menu_parent );
			$items[] = $new_item;
			$new_id = $new_item-&gt;ID;
			$ctr++;
			$terms_child = get_terms( array(&#39;taxonomy&#39; =&gt; &#39;product_cat&#39;, &#39;parent&#39;  =&gt; $term-&gt;term_id ) );
			if(!empty($terms_child))
