    import pandas as pd
    
    
    df = pd.DataFrame({
         &#39;col_to_group&#39;: [&#39;A&#39;, &#39;A&#39;, &#39;B&#39;, &#39;B&#39;, &#39;A&#39;],
         &#39;col_1&#39;: [&#39;a&#39;, &#39;b&#39;, &#39;c&#39;, &#39;a&#39;, &#39;a&#39;],
         &#39;col_2&#39;: [&#39;x&#39;, &#39;y&#39;, &#39;y&#39;, &#39;y&#39;,&#39;x&#39;],
         &#39;col_3&#39;: [.1, .2, .1, .9, .7]
    })
    
    
    for i in [&#39;col_1&#39;, &#39;col_2&#39;]:
        (pd.crosstab(df[&#39;col_to_group&#39;], df[i], margins=True, margins_name=&#39;ALL&#39;, normalize=&#39;index&#39;)
           .plot.bar(stacked=True).legend(title=i, bbox_to_anchor=(1.0, 1.0)).get_figure()
