       ClientsJson = db.Clients.Where(x =&gt; x.Name.StartsWith(term))
                                 .Select(selector: x =&gt; x.Name + ((x.Adress1 == null) ? &quot; &quot; : &quot;-&quot; + x.Adress1)).ToList();

       return Json(ClientsJson, JsonRequestBehavior.AllowGet);
    }

