     &lt;dependency&gt;
                &lt;groupId&gt;io.cucumber&lt;/groupId&gt;
                &lt;artifactId&gt;cucumber-testng&lt;/artifactId&gt;
                &lt;version&gt;${cucumber.version}&lt;/version&gt;
     &lt;/dependency&gt;

