             catch (JSONException ex) {}
             return;})
        .map(jsonArray -&gt; toJsonArray(jsonArray ))         // convert it to List
        .flatMap(List::stream)                             // to Stream&lt;Object&gt;
        .distinct()
        .collect(Collectors.toList());


