    var pointMax = 1000;

    var xScale = d3.scaleLinear()
        .domain([0, pointMax])
        .range([dims.svg_dx, dims.width - (dims.svg_dx * 2)]);


    function repaint() {
        d3.selectAll(&quot;svg&quot;).remove(); // clear out everything to start over
        for (var i = 0; i &lt; document.getElementById(&quot;numrows&quot;).value; i++) {
