        FILE *f2 = fopen(arq2, &quot;rt&quot;); // &lt;&lt;--(1)
        if(!f1 || !f2 || !final) exit(1); // &lt;&lt;--(1)
        // n1 and n2 represents the actual value that&#39;s been read. r1 and r2 the status of each file.
        int n1,n2,r1,r2,equal;
    
        r1 = fscanf(f1, &quot;%d&quot;, &amp;n1);
        r2 = fscanf(f2, &quot;%d&quot;, &amp;n2);
        while(r1&gt;0 || r2&gt;0){ // &lt;&lt;--(2)
