export function ABTest(debugMode = false) {
  const roll = Math.floor(Math.random() * 100);
  let ABTestValue = 30;

  if (debugMode) {
    console.log(
      &#39;TimedRefresh - AB Test Issue-831 (90 and above get their refresh time changed) randomized value = &#39;,
      roll
    );
  }

  const ABTestMappings = [
    {
      roll: 90,
      ABTestValue: 40
    },
