int main()
{
    // scan the input matrix
    int a, b;
    char input[256];
    scanf(&quot;%dx%d:%256[^\n]&quot;, &amp;a, &amp;b, input);

    int mat[a][b];

    // populate matrix:
    char* p = input;
    int i = 0;
    while (p != input + 256)
    {
        // scan until the character is not a separator
        if (*p == &#39;{&#39; || *p == &#39;}&#39; || *p == &#39;,&#39;)
        {
            ++p;
        }
        else
        {
            int n;
            sscanf(p, &quot;%d&quot;, &amp;n);
            mat[0][i] = n;
            ++i;

            // scan until we find a separator character
            while (p != input + 256 &amp;&amp; (*p != &#39;{&#39; &amp;&amp; *p != &#39;}&#39; &amp;&amp; *p != &#39;,&#39;))
            {
                ++p;
            }

            if (i &gt;= a*b)
