```c++
#include &lt;vector&gt;
#include &lt;functional&gt;
class Class_A
{
public:
    std::function&lt;void()&gt; getFunctionToRunLater()
    {
        return [this] () { somethingToDo(); moreThingsToDo(); };
        // Returns a lambda function that captures the this pointer,
        // so it can access the object&#39;s methods and variables.
    }

    void somethingToDo();
    void moreThingsToDo();
}

