    from uuid import uuid4
    from time import sleep
    from multiprocessing import Pool
    
    
    def send_slack_notification(message_line):
        webhoook = &#39;MY SLACK WEB HOOK&#39;
        headers = {
            &#39;Content-Type&#39;: &#39;application/json&#39;,
            &#39;User-Agent&#39;: &#39;TaskAlerter&#39;
            }
        payload = json.dumps({
            &#39;text&#39;: message_line,
            &#39;username&#39;: &#39;TaskAlerter&#39;,
            &#39;channel&#39;: &#39;#my_channel&#39;
            })
        try:
            requests.post(webhoook, headers=headers, data=payload)
        except Exception as e:
            print(&#39;Slack Message Failed: {}&#39;.format(e))
    
    
    class Task(object):
    
        def __init__(self, my_str: str):
            self.my_str = my_str
    
        def run(self) -&gt; str:
            return self.my_str.upper()
    
        def task_callback(self, x: str):
            send_slack_notification(&#39;Result: {}&#39;.format(x))
    
    
    def my_async_final_function(task_list: list):
