    def async_issuser(work: list) -&gt; str:
        job_id = str(uuid4())
        all_tasks = []
        pool = Pool(processes=5)
        for item in work:
            task = Task(item)
            async_task = pool.apply_async(task.run, callback=task.task_callback)
            all_tasks.append(async_task)
        pool.apply_async(my_async_final_function, (all_tasks, ))
        pool.close()
        return job_id

