    type Vertex [2]float64
    type Edges map[Vertex]BagOfVertices
    type BagOfVertices map[*Vertex]bool
    
    func (e *Edges) AddEdge(v1 Vertex, v2 *Vertex) {
    	edges := *e
    	if edges[v1] == nil {
    		edges[v1] = make(BagOfVertices)
    	}
    	if edges[*v2] == nil {
    		edges[*v2] = make(BagOfVertices)
    	}
    	edges[v1][v2] = true
