&lt;!-- language: lang-css --&gt;

    .row {
      display: flex;
    }

    .col {
      display: flex;
      flex-grow: 1;
      border: 1px solid purple;
      margin: 1rem;
    }

    .col-1-of-1 {
      width: calc(100% - 2rem);
      justify-content: center;
      background: lightblue;
    }

    .col-1-of-2 {
      width: calc(50% - 2rem);
      justify-content: center;
      background: orange;
    }

    .col-1-of-3 {
      width: calc(33% - 2rem);
      justify-content: center;
      background: lightgreen;
    }

    .col-1-of-4 {
      width: calc(25% - 2rem);
      justify-content: center;
      background: lightcoral;
    }

