            LinkedList list = new LinkedList();
            boolean flag = true;
            while (flag) {
                System.out.println();
                System.out.println(&quot;1. Add item to the list at start&quot;);
                System.out.println(&quot;2. Add item to the list at last&quot;);
                System.out.println(&quot;3. Add item to the list at position&quot;);
                System.out.println(&quot;4. Delete first item from the list&quot;);
                System.out.println(&quot;5. Delete last item from the list&quot;);
                System.out.println(&quot;6. Delete item from the list with given position&quot;);
                System.out.println(&quot;7. view List&quot;);
                System.out.println(&quot;8. Exit&quot;);
                System.out.println(&quot;Enter your choice: &quot;);
                int choice = scanner.nextInt();
                int position, value;

                switch(choice)
                {
                    case 1:
                        System.out.println(&quot;Enter value: &quot;);
                        value = scanner.nextInt();
                        list.insertAtFirst(value);
                        break;

                    case 2:
                        System.out.println(&quot;Enter value: &quot;);
                        value = scanner.nextInt();
                        list.insertAtLast(value);
                        break;

                    case 3:
                        System.out.println(&quot;Enter Position&quot;);
                        position = scanner.nextInt();
                        System.out.println(&quot;Enter value: &quot;);
                        value = scanner.nextInt();
                        list.insertAtPos(value, position);

                    case 4:
                        list.deleteAtfirst();
                        break;

                    case 5:
                        list.deleteAtlast();
                        break;

                    case 6:
                        System.out.println(&quot;Enter Position&quot;);
                        position = scanner.nextInt();
                        list.deleteAtPos(position);
                        break;

                    case 7:
                        list.viewList();
                        break;

                    case 8:
                        flag = false;
                        break;

                    default:
                        System.out.println(&quot;Invalid choice.&quot;);
                }
            }
        }
    }

    //Linkedlist.java
    public class LinkedList {

        private int size;
        private Node start;

        public LinkedList() {
            size = 0;
            start = null;
        }

        public boolean isEmpty()
        {
            if (start == null) {
                return true;
            }
            else {
                return false;
            }
        }

        public int getListSize() {
            return size;
        }

        public void viewList() {
            Node temp;
            temp = start;
            if (isEmpty())
            {
                System.out.println(&quot;List is empty&quot;);
            }
            else {
                for (int i=1; i&lt;=size; i++) {
                    System.out.print(temp.getData() + &quot; &quot;);
                    temp = temp.getNext();
                }
            }
        }

        public void insertAtFirst(int val) {
            Node n;
            n = new Node();
            n.setData(val);
            n.setNext(start);
            start = n;
            size++;
        }

        public void insertAtLast(int val)
        {
            Node n, temp;
            n = new Node();
            n.setData(val);
            temp = start;
            if (temp == null)
            {
                start = n;
            }
            else
            {
                while (temp.getNext() != null)
                {
                    temp = temp.getNext();
                    temp.setNext(n);
                }
                size++;
            }
        }


        public void insertAtPos(int val, int pos) {

            if (pos == 1) {
                insertAtFirst(val);
            }
            else if (pos == size + 1) {
                insertAtLast(val);
            }
            else if (pos &gt; 1 &amp;&amp; pos &lt;= size) {
                Node n, temp;
                n = new Node(val, null);
                temp = start;
                for (int i = 1; i &lt; pos - 1; i++) {
                    temp = temp.getNext();
                    n.setNext(temp.getNext());
                    temp.setNext(n);
                    size++;
                }
            }
            else
                System.out.println(&quot;Insertion is not possible at position &quot; + pos);
        }

        public void deleteAtfirst() {
            if (start == null) {
                System.out.println(&quot;List is already empty&quot;);
            }
            else {
                start = start.getNext();
                size--;
            }
        }

        public void deleteAtlast() {
            if (start == null) {
                System.out.println(&quot;List is already empty&quot;);
            }
            else if (size == 1) {
                start = null;
                size--;
            }
            else {
                Node temp;
                temp = start;
                for (int i = 1; i &lt; size - 1; i++) {
                    temp = temp.getNext();
                    temp.setNext(null);
                    size--;
                }
            }
        }

        public void deleteAtPos(int pos) {
            if (start == null) {
                System.out.println(&quot;List is already empty&quot;);
            }
            else if (pos == 1) {
                deleteAtfirst();
            }
            else if (pos &lt; 1 || pos &gt; size) {
                System.out.println(&quot;Invalid Position&quot;);
            }
            else if (pos == size) {
                deleteAtlast();
            }
            else {
                Node temp, temp1;
                temp = start;
                for (int i = 1; i &lt; pos - 1; i++) {
                    temp = temp.getNext();
                    temp1 = temp.getNext();
                    temp.setNext(temp1.getNext());
                    size--;
                }
            }
        }
    }


    //Node.java


    public class Node
    {
        private int data;
        private Node next;

        public Node()
        {
            data = 0;
            next = null;
        }

        public Node(int d, Node n)
        {
            data = d;
            next = n;
        }

        public void setData(int d)
        {
            data = d;
        }

        public void setNext(Node n)
        {
            next = n;
        }

        public int getData()
        {
            return data;
        }

        public Node getNext()
        {
            return next;
        }

    }
