Here&#39;s an example with react, redux, reselect

[Codesandbox](https://codesandbox.io/s/friendly-kapitsa-tbpcv)

```js
import React from &quot;react&quot;;
import ReactDOM from &quot;react-dom&quot;;
import { createStore } from &quot;redux&quot;;
import { Provider, connect } from &quot;react-redux&quot;;
import { createSelector } from &quot;reselect&quot;;

const initialState = {
  counter: 1
};

const reducer = (state = initialState, action) =&gt; {
  if (action.type === &quot;INCREMENT&quot;) {
    return { ...state, counter: state.counter + 1 };
  }
  if (action.type === &quot;DECREMENT&quot;) {
    return { ...state, counter: state.counter - 1 };
  }
  return state;
};

const store = createStore(reducer);

class ChildComp extends React.Component {
  handleIncrement = () =&gt; {
    this.props.increment();
  };

  handleDecrement = () =&gt; {
    this.props.decrement();
  };

  render() {
    const { counter, isEven } = this.props;
    return (
      &lt;div&gt;
        &lt;div&gt;counter: {counter}&lt;/div&gt;
        &lt;div&gt;is even: {JSON.stringify(isEven)}&lt;/div&gt;
        &lt;button onClick={this.handleIncrement}&gt;increment&lt;/button&gt;
        &lt;button onClick={this.handleDecrement}&gt;decrement&lt;/button&gt;
      &lt;/div&gt;
    );
  }
}

const getCounter = state =&gt; state.counter;
const isEvenSelector = createSelector(
  getCounter,
  counter =&gt; {
    console.log(&quot;calculate is even for&quot;, counter);
    return counter % 2 === 0;
  }
);

const mapStateToProps = state =&gt; {
  return {
    counter: state.counter,
    isEven: isEvenSelector(state)
  };
};

const mapDispatchToProps = dispatch =&gt; {
  return {
    increment: () =&gt; dispatch({ type: &quot;INCREMENT&quot; }),
    decrement: () =&gt; dispatch({ type: &quot;DECREMENT&quot; })
  };
};

const Child = connect(
  mapStateToProps,
  mapDispatchToProps
)(ChildComp);

class App extends React.Component {
  render() {
    return (
      &lt;Provider store={store}&gt;
        &lt;Child /&gt;
      &lt;/Provider&gt;
    );
  }
}

const rootElement = document.getElementById(&quot;root&quot;);
ReactDOM.render(&lt;App /&gt;, rootElement);

