	DECLARE @IdToFind VARCHAR(100)=&#39;pubmed&#39;;

	SELECT @xml.value(&#39;(/PubmedArticleSet
						/PubmedArticle
						/PubmedData
						/MedlineCitation
						/ArticleIdList
						/ArticleId[@IdType=sql:variable(&quot;@IdToFind&quot;)]
