from json import JSONEncoder


def beautify_key(str):
    index = str.index(&#39;__&#39;)
    if index &lt;= 0:
        return str

    return str[index + 2:]


class JsonEncoder(JSONEncoder):

    def default(self, o):
        return {beautify_key(k): v for k, v in vars(o).items()}
