const initCompiler = () =&gt; {
  WebpackDevServer.addDevServerEntrypoints(config, options)

  const compiler = webpack(config)

  server = new WebpackDevServer(compiler, options)

  server.listen(3000, &#39;localhost&#39;, () =&gt; {
