	for (i = 1; i &lt; N; i++)
		{
			x1 = a + ((i - 1) * w); //the left bound point
			x2 = a + (i*w); //the right bound point
			y1 = pow(x1,z - 1)*exp(-x1); //the height of our left bound
			y2 = pow(x2, z - 1)*exp(-x2); //the height of our right bound
