&lt;!-- language: xml --&gt;

    &lt;widget class=&quot;QMainWindow&quot; name=&quot;MainWindow&quot;&gt;
      &lt;!-- size policy, geometry... --&gt;
      &lt;widget class=&quot;QWidget&quot; name=&quot;centralwidget&quot;&gt;
